f6cee730-3ba2-483a-ab20-a585add2130a.script:
sudo apt -y install lolcat
sudo apt-get -y install konwert

cd ~
mkdir ascii
cd ascii

git clone https://github.com/PhMajerus/ANSI-art

cd ANSI-art/

ls | lolcat


for FILE in *.ans; do 
- echo;echo $FILE | lolcat; 
- cat "$FILE" | konwert cp437-UTF8 | sed 'H;$!d;x;s/\x1A.*$//';
- sleep 3;
-done
for FILE in *.txt; do 
- echo;echo $FILE; 
- cat "$FILE" ;
- sleep 3;
-done
